import "./App.css";
import ListComponent from "./components/ListComponent";
import { useState } from "react";
import UserBar from "./UserBar";
import CreateNewItem from "./components/CreateNewItem";
const initialListItems = [
  {
    title: "Title1",
    description: "Description1",
    author: "Author1",
    dateSet: "DateSet1",
    complete: false,
    dateComplete: "DateComplete1",
    id: 0,
  },
  {
    title: "Title2",
    description: "Description2",
    author: "Author2",
    dateSet: "DateSet2",
    complete: false,
    dateComplete: "DateComplete2",
    id: 1,
  },
  {
    title: "Title3",
    description: "Description3",
    author: "Author3",
    dateSet: "DateSet3",
    complete: false,
    dateComplete: "DateComplete3",
    id: 2,
  },
  {
    title: "Title4",
    description: "Description4",
    author: "Author4",
    dateSet: "DateSet4",
    complete: false,
    dateComplete: "DateComplete4",
    id: 3,
  },
];

function App() {
  const [listItem, setListItem] = useState(initialListItems);

  const [user, setUser] = useState("");

  const handleCompletedBox = (id) => {
    const temporaryList = listItem.map((element) => {
      if (element.id == id) {
        return {
          ...element,
          complete: !element.complete,
        };
      } else return element;
    });
    setListItem(temporaryList);
  };

  const handleUserName = (newName) => {
    setUser(newName);
  };

  const handleNewListItem = (newItem) => {
    const item = { ...newItem, author: user, complete: false };
    const newList = [item, ...listItem];
    setListItem(newList);
  };

  return (
    <div className="App">
      <UserBar user={user} handleSubmit={handleUserName} />

      {used && <CreateNewItem handleNewListItem={handleNewListItem} />}

      {user && (
        <ListComponent
          listItem={listItem}
          setListItem={setListItem}
          handleCompletedBox={handleCompletedBox}
        />
      )}
    </div>
  );
}

export default App;
