function userReducer(state, action) {
    switch (action.type) {
      case "LOGIN":
      case "REGISTER":
        return action.payload.user;
      case "LOGOUT":
        return "";
      default:
        return state;
    }
  }

  function todoReducer(state, action) {
    switch (action.type) {
      case "CREATE_TODO":
        const newToDo = {
          title: action.payload.title,
          description: action.payload.description,
          author: state.user,
          dateSet: action.payload.dateSet,
          id: action.payload.id,
          complete: false,
        };
        return [newToDo, ...state];
        case "CHECK_COMPLETE":
            const id=action.payload.id
            const temporaryList = state.listItem.map((element) => {
                if (element.id == id) {
                  let dateComplete = element.complete==false ? `Completed on: ${moment().format("dddd, MMMM Do YYYY, h:mm:ss a")}` : "";
                  return {
                    ...element,
                    complete: !element.complete,
                    dateComplete: dateComplete
                  };
                } else return element;
              });
              return temporaryList;
      default:
        return state;
    }
  }

  export default function appReducer(state, action) {
    return {
      user: userReducer(state.user, action),
      posts: todoReducer(state.posts, action),
    };
  }
