import "./App.css";
import ListComponent from "./components/ListComponent";
import { useReducer, useState } from "react";
import UserBar from "./UserBar";
import CreateNewItem from "./components/CreateNewItem";
import moment from 'moment';
import appReducer from "./Reducers";

function App() {
  // const [listItem, setListItem] = useState([]);
  // const [user, setUser] = useState("");

  const [state, dispatch] = useReducer(appReducer, {
    user: "",
    listItem: [],
  })

  const {listItem} = state;

  const handleCompletedBox = (id) => {
    const temporaryList = listItem.map((element) => {
      if (element.id == id) {
        let dateComplete = element.complete==false ? `Completed on: ${moment().format("dddd, MMMM Do YYYY, h:mm:ss a")}` : "";
        return {
          ...element,
          complete: !element.complete,
          dateComplete: dateComplete
        };
      } else return element;
    });
    setListItem(temporaryList);
  };

  const handleUserName = (newName) => {
    setUser(newName);
  };

  const handleNewListItem = (newItem) => {
    // const item = { ...newItem, author: user, complete: false };
    // const newList = [item, ...listItem];
    // setListItem(newList);
    dispatch( {type: "CREATE_TODO", ...newItem})
  };

  const handleLogout = () => {
    setUser("");
    setListItem([])
  }

  return (
    <div className="App">
      <UserBar user={state.user} handleSubmit={handleUserName} handleLogout={handleLogout} />

      {state.user && <CreateNewItem handleNewListItem={handleNewListItem} />}

      {state.user && (
        <ListComponent
          listItem={listItem}
          setListItem={setListItem}
          handleCompletedBox={handleCompletedBox}
        />
      )}
    </div>
  );
}

export default App;
